
def sum_of_top_three_following(number):
    tmp_string = str(number)
    sums = []
    i = 0
    while i < len(tmp_string)-4:
        tmp_sum_of_three = int(tmp_string[i]) * int(tmp_string[i+1]) * int(tmp_string[i+2]) * int(tmp_string[i+3]) * int(tmp_string[i+4])
        sums.append(tmp_sum_of_three)
        i += 1
    return max(sums)


def main():
    #
    user_input = int(input("Adj megy egy egész számot, és megmondom az öt egymást követő számjegy legnagyobb szorzatát!"))
    print(sum_of_top_three_following(user_input))

if __name__=="__main__":
    main()   